// src/pages/EventListingPage.js
import React, { useState } from 'react';
import EventList from '../components/EventList';
import { createEvent } from '../services/eventService'; 

const EventListingPage = () => {
  const [sortOrder, setSortOrder] = useState('asc');
  const [filterLocation, setFilterLocation] = useState('');

  const handleSortChange = () => {
    // Implement sorting logic (toggle between asc and desc)
    setSortOrder((prevOrder) => (prevOrder === 'asc' ? 'desc' : 'asc'));
  };

  const handleFilterChange = (e) => {
    // Implement filtering logic based on location
    setFilterLocation(e.target.value);
  };


  const handleCreateEvent = async () => {
    try {
      // Dummy event data for demonstration, replace it with your actual form data
      const eventData = {
        name: 'New Event',
        date: '2024-01-21',
        location: 'Sample Location',
        description: 'Sample Description',
        organizer: 'Sample Organizer',
      };

      // Call the createEvent service to create a new event
      const createdEvent = await createEvent(eventData);

      // Log the created event for demonstration (you can handle it as needed)
      console.log('Event created:', createdEvent);

      // You may also redirect to the newly created event page or update the listing, etc.
    } catch (error) {
      // Handle error (log, display a message, etc.)
      console.error('Error creating event:', error);
    }
  };

  return (
    <div>
      <h1>University Events</h1>
      <div>
        <label>
          Sort Order:
          <button onClick={handleSortChange}>Toggle</button>
        </label>
        <label>
          Filter by Location:
          <input type="text" value={filterLocation} onChange={handleFilterChange} />
        </label>
      </div>
      <EventList sortOrder={sortOrder} filterLocation={filterLocation} />
      {/* Use onClick to handle button click */}
      <button onClick={handleCreateEvent}>Create Event</button>
    </div>
  );
};

export default EventListingPage;
