// src/pages/EventCreationPage.js
import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { createEvent } from '../services/eventService';

const EventCreationPage = () => {
  const initialValues = {
    name: '',
    date: '',
    location: '',
    description: '',
    organizer: '',
  };

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Event name is required'),
    date: Yup.date().required('Event date is required'),
    location: Yup.string().required('Event location is required'),
    description: Yup.string().required('Event description is required'),
    organizer: Yup.string().required('Event organizer is required'),
  });


  // const handleFormSubmit = async (formData) => {
  //   try {
  //     const response = await fetch('http://localhost:3000', {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify(formData),
  //     });

  //     if (response.ok) {
  //       // Event creation successful
  //       console.log('Event created successfully!');
  //       // You can perform additional actions here, such as redirecting the user
  //     } else {
  //       // Handle error response
  //       console.error('Error creating event:', response.statusText);
  //       // You can display an error message to the user
  //     }
  //   } catch (error) {
  //     console.error('An error occurred while creating the event:', error);
  //     // Handle network errors or other exceptions
  //   }
  // };

  const handleFormSubmit = async (formData) => {
    try {
      // Call the createEvent function from eventService.js
      const createdEvent = await createEvent(formData);

      // Log the response for verification (you can remove this in production)
      console.log('Event created successfully:', createdEvent);

      // Redirect to the event listing page after successful event creation
      history.push('/');
    } catch (error) {
      console.error('Error creating event:', error);
      // Handle error (e.g., display an error message to the user)
    }
  };

  return (
    <div>
      <h1>Create Event</h1>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleFormSubmit}
      >
        <Form>
          <div>
            <label htmlFor="name">Event Name:</label>
            <Field type="text" id="name" name="name" />
            <ErrorMessage name="name" component="div" className="error" />
          </div>

          <div>
            <label htmlFor="date">Event Date:</label>
            <Field type="date" id="date" name="date" />
            <ErrorMessage name="date" component="div" className="error" />
          </div>

          <div>
            <label htmlFor="location">Event Location:</label>
            <Field type="text" id="location" name="location" />
            <ErrorMessage name="location" component="div" className="error" />
          </div>

          <div>
            <label htmlFor="description">Event Description:</label>
            <Field as="textarea" id="description" name="description" />
            <ErrorMessage name="description" component="div" className="error" />
          </div>

          <div>
            <label htmlFor="organizer">Event Organizer:</label>
            <Field type="text" id="organizer" name="organizer" />
            <ErrorMessage name="organizer" component="div" className="error" />
          </div>

          <button type="submit">Create Event</button>
        </Form>
      </Formik>
    </div>
  );
};

export default EventCreationPage;
